#pragma config(Motor,  port2,           LeftDrive,     tmotorVex393, openLoop)
#pragma config(Motor,  port5,           RightDrive,    tmotorVex393, openLoop)
#pragma config(Motor,  port7,           ForkliftMotor, tmotorVex393, openLoop)
#pragma config(Motor,  port8,           ScissorDown,   tmotorVex393, openLoop)
#pragma config(Motor,  port9,           ScissorUp,     tmotorVex393, openLoop)
