#pragma platform(VEX)

//Competition Control and Duration Settings
#pragma competitionControl(Competition)
#pragma autonomousDuration(20)
#pragma userControlDuration(120)

#include "Vex_Competition_Includes.c"   //Main competition background code...do not modify!

void driveMotors(int leftSpeed, int rightSpeed)
{
	motor[LeftDrive]   =  leftSpeed;      // up = CW
	motor[RightDrive]  =  -rightSpeed;    // up = CCW
}

void controlScissors()
{
	if(vexRT[Btn8U])
	{
		motor[ScissorUp] = 127;
	}
	else if(vexRT[Btn8D])
	{
		motor[ScissorUp] = -50;
	}
	else
	{
		motor[ScissorUp] = 0;
	}

	if(vexRT[Btn7D])
	{
		motor[ScissorDown] = 50;
	}
	else if(vexRT[Btn7U])
	{
		motor[ScissorDown] = -127;
	}
	else
	{
		motor[ScissorDown] = 0;
	}

}

void forklift()
{
	if(vexRT[Btn6U])
	{
		motor[ForkliftMotor] = 127;
	}
	else if(vexRT[Btn6D])
	{
		motor[ForkliftMotor] = -127;
	}
	else
	{
		motor[ForkliftMotor] = 0;
	}
}

void teleop()
{
	driveMotors(vexRT[Ch3], vexRT[Ch2]);
	controlScissors();
	forklift();
}

void singleBallStraightAuto()
{
	//drive forward for 4 seconds
	driveMotors(100, 105);
	wait1Msec(900);
	driveMotors(0, 0);
	wait1Msec(5);

	//driveMotors(-120,125); //TURNS LEFT 120?
	//wait1Msec(5000);
	//driveMotors(0,0);
	//wait1Msec(5);

	//driveMotors(-100,-105); BACKWARDS
	//wait1Msec(2300);
	//driveMotors(0,0);
	//wait1Msec(5);

	driveMotors(0, 0);
}



